    REG_DT
    , REG_ID
    , CATEGORY
    , TITLE
    , CONTENTS
    , VIEW_COUNT
    , DISPLAY_YN
)
VALUES
(
    NOW()
    , #{loginUserId}
    , #{category}
    , #{title}
    , #{contents}
    , 0
    , #{displayYn}
)

mapper.update:
UPDATE 
SET


        VIEW_COUNT = VIEW_COUNT + 1


        UDT_DT = NOW()
        , UDT_ID = #{loginUserId}
